/* Generic byte swapping functions. */

static inline
__uint16_t
_OSSwapInt16(
    __uint16_t        _data
)
{
    return ((_data << 8) | (_data >> 8));
}

static inline
__uint32_t
_OSSwapInt32(
    __uint32_t        _data
)
{

    

    __asm__ ("bswap   %0" : "+r" (_data));
    return _data;

}





    


    



static inline
__uint64_t
_OSSwapInt64(
    __uint64_t        _data
)
{
    __asm__ ("bswap   %%eax\n\t"
             "bswap   %%edx\n\t" 
             "xchgl   %%eax, %%edx"
             : "+A" (_data));
    return _data;
}




    


    
    







